    def get_transactions_by_method_id(self,address, method_id,depth,api_key="VP84ZNW3VHQ2S9JHE92VYXV96NX9E5U3VU"):
        url = f"https://api.etherscan.io/api?module=account&action=txlist&address={address}&sort=desc&apikey={api_key}&page=1&offset={depth}"
        response = requests.get(url)
        data = response.json()
        if data['status'] != '1':
            raise ValueError('Error:', data['message'])

        transactions = data['result']
        filtered_transactions = [tx for tx in transactions if tx['input'].startswith(method_id)]
        return filtered_transactions

    def decode_input_data(self,input_data):
        if input_data[0:10] not in self.SUPPORTED_METHODS.keys():
            raise ValueError("This Uniswap V2 method is currently not supported")
        decoded=self.router_contract.decode_function_input(input_data)
        return decoded

    def align_tokens_inverse(self,in_token,out_token):
        in_size=int.from_bytes(bytes.fromhex(in_token[2:]))
        out_size=int.from_bytes(bytes.fromhex(out_token[2:]))
        if in_size<out_size:
            return in_token,out_token,1
        else:
            return out_token,in_token,-1

    def get_rate_from_path(self,path):
        running_rate=1
        for j,k in enumerate(path):
            if j == len(path)-1:
                break
            token0,token1,inverse=self.align_tokens_inverse(k,path[j+1])
            pair_addr=self.factory_contract.functions.getPair(token0,token1).call()
            reserves=self.web3.eth.contract(pair_addr,abi=self.pair_abi).functions.getReserves().call(block_identifier=self.block)
            optimal_rate=self.safe_division(reserves[1],reserves[0])**inverse
            running_rate=running_rate*optimal_rate*0.997
        return running_rate

