        decoded=self.decode_input_data(input_data)
        running_rate=self.get_rate_from_path(decoded[1]['path'])
        executed_rate=self.safe_division(decoded[1]["amountOut"],decoded[1]["amountInMax"])
        slippage=1-(self.safe_division(executed_rate,running_rate))
        return slippage

